set-header.value:
@{
            // Get original Operation-Location from backend
            var op = context.Response.Headers["Operation-Location"][0];

            var original = new System.Uri(op);
            var scheme   = context.Request.OriginalUrl.Scheme;
            var host     = context.Request.OriginalUrl.Host;

            // Build new query with backendId=doc-west
            var query    = original.Query; // includes leading "?" or empty string
            var newQuery = string.IsNullOrEmpty(query)
                ? "?backendId=doc-west"
                : query + "&backendId=doc-west";

            // Compose the APIM URL (prefix /doc, keep original path)
            var newUrl = scheme + "://" + host + "/doc" + original.AbsolutePath + newQuery;

            return newUrl;
        }
